    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        // Datos
        Venta venta = leerDatos();

        // Procesar
        venta = ventaController.procesarVenta(venta);

        // Reporte
        txtImporte.setText("" + venta.getImporte());
        txtImpuesto.setText("" + venta.getImpuesto());
        txtTotal.setText("" + venta.getTotal());
    }//GEN-LAST:event_btnProcesarActionPerformed

    private void btnGrabarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGrabarActionPerformed
        // Datos
        Venta venta = leerDatos();

        // Procesar
        venta = ventaController.procesarVenta(venta);
        ventaController.registrarVenta(venta);
        
        // Limpiar formulario
        txtCliente.setText("");
        txtArticulo.setText("");
        txtPrecio.setText("");
        txtCantidad.setText("");
        txtImporte.setText("");
        txtImpuesto.setText("");
        txtTotal.setText("");
        
        
    }//GEN-LAST:event_btnGrabarActionPerformed

    private Venta leerDatos() {
        Venta venta = new Venta();
        venta.setCliente(txtCliente.getText());
        venta.setArticulo(txtArticulo.getText());
        venta.setPrecio(Double.parseDouble(txtPrecio.getText()));
        venta.setCantidad(Integer.parseInt(txtCantidad.getText()));
        return venta;
    }

